    /**
     * Install the MUMUImageModule application.
     *
     * @return boolean True on success, or false
     *
     * @throws RuntimeException Thrown if database tables can not be created or another error occurs
     */
    public function install()
    {
        $logger = $this->container->get('logger');
        $userName = $this->container->get('zikula_users_module.current_user')->get('uname');
    
        // Check if upload directories exist and if needed create them
        try {
            $container = $this->container;
            $controllerHelper = new \MU\MUImageModule\Helper\ControllerHelper($container, $container->get('translator.default'), $container->get('session'), $container->get('logger'));
            $controllerHelper->checkAndCreateAllUploadFolders();
        } catch (\Exception $e) {
            $this->addFlash(\Zikula_Session::MESSAGE_ERROR, $e->getMessage());
            $logger->error('{app}: User {user} could not create upload folders during installation. Error details: {errorMessage}.', ['app' => 'MUMUImageModule', 'user' => $userName, 'errorMessage' => $e->getMessage()]);
        
            return false;
        }
        // create all tables from according entity definitions
        try {
            $this->schemaTool->create($this->listEntityClasses());
        } catch (\Exception $e) {
            if (System::isDevelopmentMode()) {
                $this->addFlash(\Zikula_Session::MESSAGE_ERROR, $this->__('Doctrine Exception') . ': ' . $e->getMessage());
                $logger->error('{app}: User {user} could not create the database tables during installation. Error details: {errorMessage}.', ['app' => 'MUMUImageModule', 'user' => $userName, 'errorMessage' => $e->getMessage()]);
    
                return false;
            }
            $returnMessage = $this->__f('An error was encountered while creating the tables for the %s extension.', ['%s' => 'MUMUImageModule']);
            if (!System::isDevelopmentMode()) {
                $returnMessage .= ' ' . $this->__('Please enable the development mode by editing the /app/config/parameters.yml file (change the env variable to dev) in order to reveal the error details (or look into the log files at /app/logs/).');
            }
            $this->addFlash(\Zikula_Session::MESSAGE_ERROR, $returnMessage);
            $logger->error('{app}: User {user} could not create the database tables during installation. Error details: {errorMessage}.', ['app' => 'MUMUImageModule', 'user' => $userName, 'errorMessage' => $e->getMessage()]);
    
            return false;
        }
    
        // set up all our vars with initial values
        $this->setVar('pagesize', 10);
        $this->setVar('pageSizeAdminAlbums', 10);
        $this->setVar('pageSizeAdminPictures', 10);
        $this->setVar('showTitle', false);
        $this->setVar('showDescription', false);
        $this->setVar('countImageView', false);
        $this->setVar('numberParentAlbums', 1);
        $this->setVar('numberSubAlbums', 2);
        $this->setVar('numberPictures', 20);
        $this->setVar('fileSize', 102400);
        $this->setVar('minWidth', 400);
        $this->setVar('maxWidth', 0);
        $this->setVar('maxHeight', 0);
        $this->setVar('groupForCommonAlbums', );
        $this->setVar('supportCategories', false);
        $this->setVar('supportSubAlbums', false);
        $this->setVar('breadcrumbInFrontend', false);
        $this->setVar('kindOfShowSubAlbums',  'links' );
        $this->setVar('ending', 'html');
        $this->setVar('userDeletePictures', false);
        $this->setVar('slideshow1', false);
        $this->setVar('slide1Interval', 4000);
        $this->setVar('slide1Speed', 1000);
        $this->setVar('createSeveralPictureSizes', false);
        $this->setVar('widthFirst', '');
        $this->setVar('heightFirst', '');
        $this->setVar('widthSecond', '');
        $this->setVar('heightSecond', '');
        $this->setVar('widthThird', '');
        $this->setVar('heightThird', '');
        $this->setVar('shrinkPictures', false);
        $this->setVar('fileNameForTitle', true);
    
        $categoryRegistryIdsPerEntity = [];
    
        // add default entry for category registry (property named Main)
        $categoryApi = new \MU\MUImageModule\Api\CategoryApi($this->container, new \MU\MUImageModule\MUMUImageModule());
        $categoryGlobal = CategoryUtil::getCategoryByPath('/__SYSTEM__/Modules/Global');
    
        $registry = new CategoryRegistryEntity();
        $registry->setModname('MUMUImageModule');
        $registry->setEntityname('Album');
        $registry->setProperty($categoryApi->getPrimaryProperty(['ot' => 'Album']));
        $registry->setCategory_Id($categoryGlobal['id']);
    
        try {
            $entityManager = $this->container->get('doctrine.entitymanager');
            $entityManager->persist($registry);
            $entityManager->flush();
        } catch (\Exception $e) {
            $this->addFlash(\Zikula_Session::MESSAGE_ERROR, $this->__f('Error! Could not create a category registry for the %s entity.', ['%s' => 'album']));
            $logger->error('{app}: User {user} could not create a category registry for {entities} during installation. Error details: {errorMessage}.', ['app' => 'MUMUImageModule', 'user' => $userName, 'entities' => 'albums', 'errorMessage' => $e->getMessage()]);
        }
        $categoryRegistryIdsPerEntity['album'] = $registry->getId();
    
        // create the default data
        $this->createDefaultData($categoryRegistryIdsPerEntity);
    
        // install subscriber hooks
        $this->hookApi->installSubscriberHooks($this->bundle->getMetaData());
        
    
        // initialisation successful
        return true;
    }
    
