    /**
     * Uninstall MUMUImageModule.
     *
     * @return boolean True on success, false otherwise
     *
     * @throws RuntimeException Thrown if database tables or stored workflows can not be removed
     */
    public function uninstall()
    {
        $logger = $this->container->get('logger');
        $userName = $this->container->get('zikula_users_module.current_user')->get('uname');
    
        // delete stored object workflows
        $result = Zikula_Workflow_Util::deleteWorkflowsForModule('MUMUImageModule');
        if ($result === false) {
            $this->addFlash(\Zikula_Session::MESSAGE_ERROR, $this->__f('An error was encountered while removing stored object workflows for the %s extension.', ['%s' => 'MUMUImageModule']));
            $logger->error('{app}: User {user} could not remove stored object workflows during uninstallation.', ['app' => 'MUMUImageModule', 'user' => $userName]);
    
            return false;
        }
    
        try {
            $this->schemaTool->drop($this->listEntityClasses());
        } catch (\Exception $e) {
            if (System::isDevelopmentMode()) {
                $this->addFlash(\Zikula_Session::MESSAGE_ERROR, $this->__('Doctrine Exception') . ': ' . $e->getMessage());
                $logger->error('{app}: User {user} could not remove the database tables during uninstallation. Error details: {errorMessage}.', ['app' => 'MUMUImageModule', 'user' => $userName, 'errorMessage' => $e->getMessage()]);
    
                return false;
            }
            $this->addFlash(\Zikula_Session::MESSAGE_ERROR, $this->__f('An error was encountered while dropping tables for the %s extension.', ['%s' => 'MUMUImageModule']));
            $logger->error('{app}: User {user} could not remove the database tables during uninstallation. Error details: {errorMessage}.', ['app' => 'MUMUImageModule', 'user' => $userName, 'errorMessage' => $e->getMessage()]);
    
            return false;
        }
    
        // uninstall subscriber hooks
        $this->hookApi->uninstallSubscriberHooks($this->bundle->getMetaData());
        
    
        // remove all module vars
        $this->delVars();
    
        // remove category registry entries
        ModUtil::dbInfoLoad('ZikulaCategoriesModule');
        DBUtil::deleteWhere('categories_registry', 'modname = \'MUMUImageModule\'');
    
        // remove all thumbnails
        $manager = $this->container->get('systemplugin.imagine.manager');
        $manager->setModule('MUMUImageModule');
        $manager->cleanupModuleThumbs();
    
        // remind user about upload folders not being deleted
        $uploadPath = $this->container->getParameter('datadir') . '/MUMUImageModule/';
        $this->addFlash(\Zikula_Session::MESSAGE_STATUS, $this->__f('The upload directories at [%s] can be removed manually.', ['%s' => $uploadPath]));
    
        // uninstallation successful
        return true;
    }
    
    /**
     * Build array with all entity classes for MUMUImageModule.
     *
     * @return array list of class names
     */
    protected function listEntityClasses()
    {
        $classNames = [];
        $classNames[] = 'MU\MUImageModule\Entity\AlbumEntity';
        $classNames[] = 'MU\MUImageModule\Entity\AlbumCategoryEntity';
        $classNames[] = 'MU\MUImageModule\Entity\PictureEntity';
    
        return $classNames;
    }
    
    /**
     * Create the default data for MUMUImageModule.
     *
     * @param array $categoryRegistryIdsPerEntity List of category registry ids
     *
     * @return void
     */
    protected function createDefaultData($categoryRegistryIdsPerEntity)
    {
        $entityClass = 'MU\MUImageModule\Entity\AlbumEntity';
        $entityManager = $this->container->get('doctrine.entitymanager');
        $entityManager->getRepository($entityClass)->truncateTable();
        $entityClass = 'MU\MUImageModule\Entity\PictureEntity';
        $entityManager = $this->container->get('doctrine.entitymanager');
        $entityManager->getRepository($entityClass)->truncateTable();
    }
