namespace MU\ImageModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use MU\ImageModule\Entity\AlbumEntity;

/**
 * Event base class for filtering album processing.
 */
class AbstractFilterAlbumEvent extends Event
{
    /**
     * @var AlbumEntity Reference to treated entity instance.
     */
    protected $album;

    public function __construct(AlbumEntity $album)
    {
        $this->album = $album;
    }

    public function getAlbum()
    {
        return $this->album;
    }
}
