/**
 * Replaces a given needle id by the corresponding content.
 *
 * @param array $args Arguments array
 *     int nid The needle id
 *
 * @return string Replaced value for the needle
 */
function MUImageModule_needleapi_avatar_base($args)
{
    // Get arguments from argument array
    $nid = $args['nid'];
    unset($args);

    // cache the results
    static $cache;
    if (!isset($cache)) {
        $cache = [];
    }

    $translator = \ServiceUtil::get('translator.default');

    if (empty($nid)) {
        return '<em>' . \DataUtil::formatForDisplay(__('No correct needle id given.')) . '</em>';
    }

    if (isset($cache[$nid])) {
        // needle is already in cache array
        return $cache[$nid];
    }

    if (!\ModUtil::available('MUImageModule')) {
        $cache[$nid] = '<em>' . \DataUtil::formatForDisplay($translator->__f('Module %s is not available.', ['%s' => MUImageModule'])) . '</em>';

        return $cache[$nid];
    }

    // strip application prefix from needle
    $needleId = str_replace('MUIMAGE', '', $nid);

    $router = \ServiceUtil::getService('router');

    if ($needleId == 'AVATARS') {
        if (!\SecurityUtil::checkPermission('MUImageModule:Avatar:', '::', ACCESS_READ)) {
            $cache[$nid] = '';

            return $cache[$nid];
        }
    }

    $cache[$nid] = '<a href="' . $router->generate('muimagemodule_avatars_view') . '" title="' . $translator->__('View avatars') . '">' . $translator->__('Avatars') . '</a>';
    $needleParts = explode('-', $needleId);
    if ($needleParts[0] != 'AVATAR' || count($needleParts) < 2) {
        $cache[$nid] = '';

        return $cache[$nid];
    }

    $permissionApi = \ServiceUtil::get('zikula_permissions_module.api.permission');
    $entityId = (int)$needleParts[1];

    if (!$permissionApi->hasPermission('MUImageModule:Avatar:', $entityId . '::', ACCESS_READ)) {
        $cache[$nid] = '';

        return $cache[$nid];
    }

    $selectionHelper = \ServiceUtil::get('mu_image_module.selection_helper');
    $entity = $selectionHelper->getEntity('avatar', $entityId);
    if (null === $entity) {
        $cache[$nid] = '<em>' . $translator->__f('Avatar with id %s could not be found', ['%s' => $entityId]) . '</em>';

        return $cache[$nid];
    }

    $title = $entity->getTitleFromDisplayPattern();
    $cache[$nid] = '<a href="' . $router->generate('muimagemodule_avatars_display', ['id' => $entityId]) . '" title="' . str_replace('"', '', $title) . '">' . $title . '</a>';

    return $cache[$nid];
}
