/**
 * The muimageCheckGroupMember method is checking if the actual user
 * is in the same group as user created the rlevant item
 *
 * @return bool
 */
function smarty_function_muimageCheckGroupMember($params, $view)
{	
    $createdUserId = $params['createdUserId'];
	$result = MUImage_Util_View::CheckGroupMember($createdUserId);
	
	if (array_key_exists('assign', $params)) {
	    $view->assign($params['assign'], $result);
	    return;
	}
	return $result;
}
