/**
 * The muimageImageMeta function displays a meta datas of an image.
 *
 * @param  string    $imageurl      The url to the image (including file name).
 *
 * @return string the meta datas.
 */
function smarty_function_muimageImageMeta2($params, $view)
{
    /**
     * By overriding this plugin or the util method called below you may add further thumbnail arguments
     * based on custom conditions.
     */
    
    $filePath = $params['filePath'];
    
        $imgInfo = getimagesize($filePath);
        if (!is_array($imgInfo)) {
            return $meta;
        }
    
        $meta['width'] = $imgInfo[0];
        $meta['height'] = $imgInfo[1];
    
    if (array_key_exists('assign', $params)) {
        $view->assign($params['assign'], $meta);
    
        return;
    }
    
    return $meta;
}
